input = """ULL
RRDDD
LURDL
UUUUD"""

from copy import deepcopy
from numpy import matrix
import re

class KeyPad:
	def __init__(self):
		self.x = 1
		self.y = 1
		self.pad = matrix([[1,4,7],[2,5,8],[3,6,9]])
	def __str__(self):
		return "%i" % (self.getValue())
	def __eq__(self,other):
		return self.x == other.x and self.y == other.y
	def getValue(self):
		return self.pad.item(self.x,self.y)
	def getCode(self, input):
		input = input.split("\n")
		code = []
		for row in input:
			print(row)
			for step in row:
				if step == "U":
					self.y = self.step(self.y,-1)
				elif step == "R":
					self.x = self.step(self.x, 1)
				elif step == "D":
					self.y = self.step(self.y, 1)
				elif step == "L":
					self.x = self.step(self.x,-1)
			code.append(self.getValue())
		return code
	def step(self, cur, step):
		if 0 <= cur+step <= 2:
			cur+=step
		return cur

pad = KeyPad()
code = pad.getCode(input)
print (code)
